            />
          </template>

          <div class="m-title m-f14" :class="{ 'mg-top-10': payIdx === 1 }">
            {{ $t('transfer_amount') }}(<span class="m-c-red"
              >{{ getMinPrice }}~{{ getMaxPrice }}</span
            >)
          </div>
